var xmlshim = require('../');
var fs = require('fs');
var dp, xs, doc;

exports.setUp = function(callback) {
    xs = new xmlshim.XMLSerializer();
    dp = new xmlshim.DOMParser();
    callback();
};

exports['should write a document after reading it'] = function(test) {
    var input = fs.readFileSync('./test/fixtures/logo-1.svg', { encoding: 'utf-8' });
    var doc = dp.parseFromString(input, 'text/xml');

    result = xs.serializeToString(doc);

    test.done();
};
